package week1.day1;

public class CheckIfNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -2;
		int b = 9;
		if(a>0)
		{
			System.out.println("Positive Number");
		}
		else if(b<0)
		{
			System.out.println("Negative Number");
		}
	}

}
